  public static int directions(String dir) {
    int floor = 0;
    int length = dir.length();

    for (int i = 0; i < length; i++) {
      if (dir.charAt(i) == '(') {
        floor++;
      }

      if (dir.charAt(i) == ')') {
        floor--;
      }
    }

    return floor;
  }


  public static int basement(String dir) {
    int floor = 0;
    int length = dir.length();
    int position = 0;

    for (int i = 0; i < length; i++) {
      if (floor == -1) {
        position = i;
        break;
      }
      if (dir.charAt(i) == '(') {
        floor++;
      }

      if (dir.charAt(i) == ')') {
        floor--;
      }
    }

    return position;

  }




