  public static int directions(String dir) {
    int floor = 0;
    int length = dir.length();

    for (int i = 0; i < length; i++) {
      if (dir.charAt(i) == '(') {
        floor++;
      }

      if (dir.charAt(i) == ')') {
        floor--;
      }
    }

    return floor;
  }




